import React, {useState} from 'react'
import './Gallery.css'
import {data} from '../../data'
import {PhotoCard} from '../PhotoCard'
import {Modal} from '../Modal'

export const Gallery = () => {

    const [currentPhoto, setCurrentPhoto] = useState()

    const onNext = () => {
   setCurrentPhoto({photo: data[currentPhoto.id+1]})
    }

    return  <>
    {currentPhoto && <Modal onNext={onNext} setCurrentPhoto={setCurrentPhoto} currentPhoto={currentPhoto}/>}
    <div className="gallery">
        {
            data.map((obj, id) => {
                return <PhotoCard setCurrentPhoto={setCurrentPhoto} photo={obj.photo} id={id} key={id} />
            })
        }
    </div>
    </>
}
